// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@matterlabs/zksync-contracts/l2/system-contracts/Constants.sol";
import "@matterlabs/zksync-contracts/l2/system-contracts/SystemContractsCaller.sol";
import "./PaymasterFactoryStorage.sol";

contract SPaymasterFactory {
    bytes32 public BytecodeHash;
    SPaymasterStorage paymasterStorage;

    /**
     * @notice creates new instance of paymasterStorage at factory creation time (see IPaymasterStorage.sol)
     */
    constructor(bytes32 _bytecodeHash) {
        BytecodeHash = _bytecodeHash;
        paymasterStorage = new SPaymasterStorage();
    }

    /**
     * @notice This function uses Create2 to deploy a paymaster contract
     * @dev uses SystemcontractCaller makes a call to the DEPLOYER_SYSTEM_CONTRACT and deploys paymaster contract with bytecode (BytecodeHash).
     * @param salt - create2 salt
     * @param owner - deployer address or whomever will be assumed paymaster owner
     * @param metadata - metadata to be passed to PaymasterStorage
     * @param maxNonce - used in Paymaster to allow paymaster useable by only new wallets
     * @param useCustomToken - tells paymaster an ERC20 should be used for Validation (see Paymaster.sol)
     * @param amount - (optional) tells paymaster to require to hold x amount of ERCX token
     * @param validationAddress - ERC20 Fee reciever. A specific address that should hold high level paymaster roles (e.g protocol governance contract)
     */
    function deployPaymaster(
        // used in create2
        bytes32 salt,
        address owner,
        // used in paymasterStorage
        bytes memory metadata,
        // parsed to paymaster constructor
        uint64 maxNonce,
        bool useCustomToken,
        address tokenAddress,
        uint192 amount,
        address validationAddress
    ) external returns (address accountAddress) {
        bytes memory returnData = SystemContractsCaller.systemCall(
            uint32(gasleft()),
            address(DEPLOYER_SYSTEM_CONTRACT),
            0,
            abi.encodeCall(
                DEPLOYER_SYSTEM_CONTRACT.create2,
                (
                    salt,
                    BytecodeHash,
                    abi.encode(
                        owner,
                        maxNonce,
                        useCustomToken,
                        tokenAddress,
                        amount,
                        validationAddress
                    )
                )
            )
        );

        (accountAddress, ) = abi.decode(returnData, (address, bytes));
        storePaymaster(owner, accountAddress, metadata);
    }

    function storePaymaster(
        address owner,
        address paymasterAddr,
        bytes memory metadata
    ) internal {
        paymasterStorage.storePaymaster(owner, paymasterAddr, metadata);
    }
}

contracts/factory/PaymasterFactoryStorag.content:
// SPDX-License-Identifier: SEE LICENSE IN LICENSE
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import "../interface/IPaymaster.sol";

/// @title Paymster Storage Contract
/// @author (github@peteruche21)
/// @notice Stores paymasters deployed by the Paymaster factory.
/// @dev includes Paymaster Contract address and paymaster metadata, paymaster metadata consists of owner address, name and logoHash, creation detials. Paymaster metadata is stored offchain
contract SPaymasterStorage {
    struct PaymasterMeta {
        address contract_address;
        bytes metadata;
    }
    mapping(address => uint256) private ownerToPaymaster;
    PaymasterMeta[] internal Paymasters;

    /**
     * @notice Stores Paymaster to Array Paymasters
     * @dev External function! must be called from the factory, does not guarantee ERC165 check.
     * @param owner - the deployer of this paymaster
     * @param paymasterAddr - the address of paymaster to store
     * @param metadata - offchain metadata cid/hash (name, owner, logoHash: paymaster logo, creation details)
     */
    function storePaymaster(
        address owner,
        address paymasterAddr,
        bytes memory metadata
    ) external {
        // check if paymasterAddr surpports the ISPaymaster Interface
        // not a valid check, cause you can just return bool==true from attacking contract.
        // but lets keep it. possibly allow only calls from factory.
        require(
            IERC165(paymasterAddr).supportsInterface(
                type(ISPaymaster).interfaceId
            ),
            "not a valid paymaster contract"
        );
        ownerToPaymaster[owner] = Paymasters.length;
        Paymasters.push(
            PaymasterMeta({contract_address: paymasterAddr, metadata: metadata})
        );
    }

    function getPaymasterByOwner(address owner)
        external
        view
        returns (PaymasterMeta memory)
    {
        return (Paymasters[ownerToPaymaster[owner]]);
    }

    function getAllPaymasters() external view returns (PaymasterMeta[] memory) {
        return Paymasters;
    }
}

